import { defineStore } from 'pinia'
import { shallowRef, ref } from 'vue'
import { sendApi } from '@/utils/api'

export const useManageNewsStore = defineStore('manageNews', () => {
  const newsList = shallowRef([])
  const loading = ref(false)

  const extractEssential = (list) =>
    list.map((item) => ({
      id: item.id,
      status: item.status,
      reportLength: item.report?.length || 0,
      updated_at: item.report?.[0]?.updated_at ?? null,
    }))

  const isSameData = (a, b) => {
    return JSON.stringify(extractEssential(a)) === JSON.stringify(extractEssential(b))
  }

  const loadNews = async () => {
    loading.value = true
    try {
      const res = await sendApi({ control: 'news', action: 'user_news' })
      if (res.status === 'success') {
        const news = res.data || []
        if (!isSameData(newsList.value, news)) {
          newsList.value = news
        }
      } else {
        alert('خطا در بارگذاری اخبار: ' + res.message)
      }
    } catch (error) {
      alert('خطا در ارتباط با سرور: ' + error.message)
    }
    loading.value = false
  }

  const restoreNews = async (id) => {
    try {
      const res = await sendApi({ control: 'news', action: 'restore_news', data: { news_id: id } })
      if (res.status === 'success') {
        await loadNews()
        return true
      } else {
        alert('خطا در پخش مجدد خبر: ' + res.message)
        return false
      }
    } catch (error) {
      alert('خطا در ارتباط با سرور: ' + error.message)
      return false
    }
  }

  const deleteNews = async (id) => {
    try {
      const res = await sendApi({ control: 'news', action: 'delete_news', data: { news_id: id } })
      if (res.status === 'success') {
        newsList.value = newsList.value.filter(news => news.id !== id)
        return true
      } else {
        alert('خطا در حذف خبر: ' + res.message)
        return false
      }
    } catch (error) {
      alert('خطا در ارتباط با سرور: ' + error.message)
      return false
    }
  }

  let pollingInterval = null
  const startPolling = () => {
    if (pollingInterval) return
    loadNews()
    pollingInterval = setInterval(() => {
      loadNews()
    }, 10000)
  }

  const stopPolling = () => {
    if (pollingInterval) {
      clearInterval(pollingInterval)
      pollingInterval = null
    }
  }

  return {
    newsList,
    loading,
    loadNews,
    deleteNews,
    restoreNews,
    startPolling,
    stopPolling,
  }
})
