import { defineStore } from 'pinia'
import { ref } from 'vue'
import { sendApi } from '@/utils/api'
export const usePlaceStore = defineStore('place', () => {
  const allPlaces = ref([])
  const categories = ref([])
  const userCoordinate = ref([])
  const userAccountId = ref(null)
  const categoryLoading = ref(false)
  const placeOffset = ref(0)
  const categoryOffset = ref(0)
  const hasMorePlaces = ref(true)
  const hasMoreCategories = ref(true)
  const fetchCategoriesPaginated = async (offset = 0, limit = 20) => {
    const res = await sendApi({
      control: 'place',
      action: 'get_places_category',
      data: { offset, limit }
    })
    if (res.status === 'success') {
      if (Array.isArray(res.data?.items)) {
        if (offset === 0) categories.value = res.data.items
        else categories.value.push(...res.data.items)
        hasMoreCategories.value = res.data.has_more
        categoryOffset.value += res.data.items.length
      }
    }
  }
  const fetchPlacesPaginated = async ({ offset = 0, category_id = null, limit = 10 }) => {
    const res = await sendApi({
      control: 'place',
      action: 'get_places',
      data: { offset, category_id, limit }
    })
    if (res.status === 'success') {
      if (Array.isArray(res.data?.items)) {
        if (offset === 0) allPlaces.value = res.data.items
        else allPlaces.value.push(...res.data.items)
        hasMorePlaces.value = res.data.has_more
        placeOffset.value += res.data.items.length
      }
    }
  }
  const fetchCategories = async (query) => {
    categoryLoading.value = true
    try {
      const res = await sendApi({ control: 'place', action: 'get_places_category' , data: { search: query } })
      if (res.status === 'success' && Array.isArray(res.data)) {
        categories.value=res.data
      } else {
        console.error('خطا در دریافت داده‌ها:', res.message)
      }
    } catch (e) {
      console.error('خطا در ارتباط:', e)
    }finally {
      categoryLoading.value = false
    }
  }
  const submitPlace = async (placeData) => {
    try {
      const res = await sendApi({
        control: 'place',
        action: 'add_place',
        data: placeData
      })
      if (res.status === 'success') {
        await fetchAllPlaces()
      }
      return res
    } catch (error) {
      return { status: 'error', message: 'خطا در ارسال درخواست' }
    }
  }
  return {
    allPlaces,
    categories,
    userCoordinate,
    userAccountId,
    categoryLoading,
    placeOffset,
    categoryOffset,
    hasMorePlaces,
    hasMoreCategories,
    fetchCategoriesPaginated,
    fetchPlacesPaginated,
    fetchCategories,
    submitPlace
  }
})
