import { ref, watch, onMounted, onBeforeUnmount } from 'vue'

export function useInfiniteScroll(fetchFn, {
    limit = 10,
    total = null,
    immediate = true,
} = {}) {
    const items = ref([])
    const loading = ref(false)
    const offset = ref(0)
    const canLoadMore = ref(true)
    const loadMoreTrigger = ref(null)
    let observer = null

    const loadMore = async () => {
        if (loading.value || !canLoadMore.value) return
        loading.value = true

        try {
            const result = await fetchFn({ limit, offset: offset.value })
            const newItems = Array.isArray(result) ? result : []
            const filteredNewItems = newItems.filter(item => item != null)

            if (filteredNewItems.length < limit || (total && items.value.length + filteredNewItems.length >= total)) {
                canLoadMore.value = false
            }

            items.value.push(...filteredNewItems)
            offset.value += filteredNewItems.length
        } catch (error) {
            console.error('Error loading more items:', error)
        } finally {
            loading.value = false
        }
    }

    const setupObserver = () => {
        if (observer) observer.disconnect()

        observer = new IntersectionObserver(async ([entry]) => {
            if (entry.isIntersecting && canLoadMore.value && !loading.value) {
                await loadMore()
            }
        }, {
            root: null,
            rootMargin: '0px',
            threshold: 1.0,
        })

        if (loadMoreTrigger.value) {
            observer.observe(loadMoreTrigger.value)
        }
    }

    // cleanup on unmount
    onBeforeUnmount(() => {
        if (observer) observer.disconnect()
    })

    if (immediate) loadMore()

    return {
        items,
        loading,
        canLoadMore,
        loadMore,
        loadMoreTrigger,
        setupObserver,
    }
}
