import { defineStore } from 'pinia'
import { ref } from 'vue'
import { sendApi } from '@/utils/api'
export const useCartableStore = defineStore('cartable', () => {
    const cartables = ref([])
    const loading = ref(true)
    const loadCartables = async () => {
        try {
            const res = await sendApi({ control: 'news', action: 'get_cartables' })
            if (res.status === 'success') {
                cartables.value = res.data || []
            } else {
                alert('خطا در دریافت کارتابل‌ها: ' + res.message)
            }
        } catch (error) {
            alert('ارتباط با سرور قطع شد: ' + error.message)
        }
        loading.value = false
    }
    const getCartableById = (id) => {
        return cartables.value.find(item => item.id === parseInt(id))
    }
    return {
        cartables,
        loading,
        loadCartables,
        getCartableById,
    }
})
